import com.eviware.soapui.support.GroovyUtils
import com.eviware.soapui.support.types.StringToStringsMap

def groovyUtils = new GroovyUtils(context);
def xmlHolder = groovyUtils.getXmlHolder(messageExchange.responseContent);


//----------------Validate Soap Fault----------------
//Add name space using namespaces variable
//xmlHolder.namespaces["xmlns"] = "http://xmlsoccer.com/"
xmlHolder.namespaces["soap"] = "http://schemas.xmlsoap.org/soap/envelope/"

String actualfaultCode = xmlHolder.getNodeValue("/soap:Envelope/soap:Body/soap:Fault/faultcode")
String expectedFaultCode = null

if(actualfaultCode == null)
	log.info "Validate Soap Fault is passed"
else	
	log.error "Validate Soap Fault is failed as FaultCode is not null. Actual value is: " + actualfaultCode

//Define Groovy Utils and holder for validating the XML reponse content
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def holder = groovyUtils.getXmlHolder(messageExchange.responseContent)

//Define the NameSpace
holder.namespaces["ns1"] = "http://tempuri.org/"

//Get the Value of the Node 'ConversionRateResult' and assign to a variable
Float conversionRate = Float.parseFloat(holder.getNodeValue("//ns1:GetConversionRateResult"))

//print the value of the conversion rate in the Output pane
log.info "The conversion value from USD to INR is " + conversionRate

//Comparing the value to print 'Pass' or 'Fail'
if(conversionRate=="68.62803")
{ log.info "Pass" }
else
{ log.info "fail"}

