//Declare common functions
def myCustomAssertion(actualVal, expectedVal, testCaseName){
	if(actualVal == expectedVal)
		log.info  "$testCaseName is passed"
	else
		log.error  "$testCaseName is failed. Actual Value: ${actualVal}; Expected Value: ${expectedVal}"
}

//Below getTotalElementsInSetIgnorSemiColn will iterate through set, ignore semicolon and return total elements
int getTotalElementsInSetIgnorSemiColn(Set<String> set) {
	int totalElem = 0
	for(String strObj : set) {
		totalElem = totalElem + (strObj.split(";").length)
	}
	return totalElem;
}



//Declare nodes
def matchNode_AbsPath_XSlurp = xmlSlurper.Body
	.GetFixturesByLeagueAndSeasonResponse
	.GetFixturesByLeagueAndSeasonResult
	.'XMLSOCCER.COM'
	.Match	//returns a NodeChildren
def matchNode_RelPath_XSlurp = xmlSlurper.Body
."**" //even depthFirst() method could be used
.findAll {node -> node.name()=='Match'}	//findAll returns a List

def matchNode_RelPath_XPath = "//*:Match"

/*----------Validate total number of matches----------*/
def matchNodes_StrArry = xmlHolder.getNodeValues(matchNode_RelPath_XPath)
