def maxGoals = matchNode_RelPath_XSlurp		
		.HomeGoals
		.max { it -> it.text().toInteger() }		//find max no of HomeGoals scored
		.text()
		.toInteger()		
//Get List of HomeGoal nodes with max number of goals
def listOfHomeGoalNodes = matchNode_RelPath_XSlurp
	.HomeGoals.findAll { node -> node.text().toInteger() == maxGoals }
//Transform above HomeGoal node list to return HomeTeam nodes
def actualListOfHomeTeamNodes = listOfHomeGoalNodes
	.collect { node -> node.parent().HomeTeam.text() }
	.unique()	//return unique values only	
