context
.expand('${StepProperty_ExpectedValue#expectedListOfHomeTeamNodes}')
.split(",")
.collect{ it -> it.trim() }
myCustomAssertion(actualListOfHomeTeamNodes, expectedListOfHomeTeamNodes, "Get team scoring max number of home goals")

/*----------Get total no of matches played vs locations----------*/
def locationNodeList = matchNode_RelPath_XSlurp.Location		
def locationCount_GroupBy = locationNodeList.groupBy({loc -> loc.toString()})
//locationCount_GroupBy.each { k,v -> log.info("Country: $k; Number of matches: $v.size()" ) }
myCustomAssertion(locationCount_GroupBy, locationCount_GroupBy, "Get total no of matches played vs locations")

/*----------Get HomeTeam name with most no of HomeTeamYellowCardDetails----------*/
