def hmTmClos1 = {hmTm1 -> hmTm1.toString()}	//To GroupBy HomeTeam
def hmTmClos2 = {hmTm1 -> hmTm1.parent().HomeTeamYellowCardDetails.text()} //To GroupBy HomeTeamYellowCardDetails
def homeMaxYelloCard_GroupBy = homeTeamList.groupBy(hmTmClos1, hmTmClos2)

//homeMaxYelloCard_GroupBy is a map as follows:
//Key = HomeTeam
//Value = [Key=HomeTeamYellowCardDetails : Value=HomeTeam]
