import java.util.concurrent.atomic.AtomicInteger;

import org.apache.poi.ss.usermodel.Cell;
import org.apache.poi.ss.usermodel.Row;
import org.apache.poi.ss.usermodel.Sheet;
import org.apache.poi.ss.usermodel.Workbook;
import org.apache.poi.ss.usermodel.WorkbookFactory;
import org.apache.poi.xssf.usermodel.XSSFCell;
import org.apache.poi.xssf.usermodel.XSSFSheet;
import org.apache.poi.xssf.usermodel.XSSFWorkbook;

public class ExcelFileReader {
	private Workbook workbook;
	private Sheet sheet;
	private InputStream fis;
	private String excelPath;

	def log
	def context
	def testRunner

	private static ExcelFileReader excelFileReader = null;

	private ExcelFileReader( logP, contextP, testRunnerP) {		//ePath,
		this.log = logP
		this.context = contextP
		this.testRunner = testRunnerP		
	}

	public static ExcelFileReader getInstance(log, context, testRunner) {
		synchronized(ExcelFileReader.class) {
			if(excelFileReader == null) {
				excelFileReader = new ExcelFileReader(log, context, testRunner);
			}
		}
		return excelFileReader;
	}


	public void setWorkbook(ePath) {		
		this.excelPath = ePath;
		File file = new File(excelPath);
		try{
			fis = new FileInputStream(file);
			//			workbook = new XSSFWorkbook(fis);
			workbook = WorkbookFactory.create(fis);
		}catch(Exception e){
			e.printStackTrace();
		}		
	}
	public String readSheet_IndxRowCol(int sheetIndex, int rowId, int colId) {
		sheet = workbook.getSheetAt(sheetIndex);
		Cell cell = sheet.getRow(rowId).getCell(colId);
		return cell.toString();
	}
	public void writeSheet_IndxRowCol(int sheetIndex, int rowId, int colId, String cellVal) {
		sheet = workbook.getSheetAt(sheetIndex);
//		System.out.println(sheet.getSheetName());
		Row row = sheet.getRow(rowId);
		if(row == null) row = sheet.createRow(rowId);
		Cell cell = row.getCell(colId);
		if(cell == null) cell = row.createCell(colId);
		cell.setCellValue(cellVal);
//		System.out.println(cell);
		writeWorkbook();
	}
	public void writeSheet_NameRowCol(String sheetName, int rowId, int colId, String cellVal) {
		sheet = workbook.getSheet(sheetName);
		int sheetIndx =	sheet.getWorkbook().getSheetIndex(sheet);
		writeSheet_IndxRowCol(sheetIndx, rowId, colId, cellVal)
	}

	private void writeWorkbook() {
		try {
			FileOutputStream fos = new FileOutputStream(excelPath)
			workbook.write(fos);

		} catch (IOException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}finally {
		}
	}

	public void closeWorkbook() {
		fis.close();
		workbook = null;
	}

	public void writeSheet_IndxMapOfList(HashMap<String, ArrayList<String>> map, int sheetIndex) {
		AtomicInteger rowId = new AtomicInteger(0);
		AtomicInteger colId = new AtomicInteger(-1);


		map.each{k ->
			writeSheet_IndxRowCol(sheetIndex, 0, colId.incrementAndGet(), k.getKey())
			rowId.set(0);
			k.getValue().each {v ->
				writeSheet_IndxRowCol(sheetIndex, rowId.incrementAndGet(), colId.get(), v)
			}

		}
	}

}


