public class Logger{
	def log
	def context
	def testRunner
	def results
	def fPath = "E:\\atul\\personal\\SOAProjects\\TestData\\Results.xlsx"
	def resultSheetIndx = 1
	def resultSheetName = "RunResult"
	def status
	def testStepName
	def testCaseName
	def failMsg = ""

	def Logger(logP, contextP, testRunnerP){
		this.log = logP
		this.context = contextP
		this.testRunner = testRunnerP

		// get object of ExcelFileReader class using getExcelFileReader() method
		context.setProperty("excelFileReaderLogger",context.globalContext.getExcelFileReader())
		context.excelFileReaderLogger.setWorkbook(fPath)
		testCaseName = testRunner.testCase.name
	}
	def writeResults(){		
		results = testRunner.results
		def rowId = 0

		def c = 0		
		while(c < results.size()){
			status = results[c].status.toString()
			testStepName = results[c].testStep.name
			for(msg in results[c].messages){
				failMsg = failMsg + msg + "\n"
			}
			c++

			def colId = 0
			context.excelFileReaderLogger.writeSheet_NameRowCol(resultSheetName, ++rowId, colId, testCaseName)
			context.excelFileReaderLogger.writeSheet_NameRowCol(resultSheetName, rowId, ++colId, testStepName)
			context.excelFileReaderLogger.writeSheet_NameRowCol(resultSheetName, rowId, ++colId, status)
			context.excelFileReaderLogger.writeSheet_NameRowCol(resultSheetName, rowId, ++colId, failMsg)
//			log.info "Status = " + status
//			log.info "TestStep Name = " + testStepName
//			log.info "Fail message = " + failMsg
		}		
	}	
}

//Set Logger context
log.info "Logger called from testCase: " + testRunner.testCase.name
context.setProperty( "logger", new Logger( log, context, testRunner ) )

public class GlobalContext{
	def log
	def context
	def testRunner
	def comlibrary

	def GlobalContext(logP, contextP, testRunnerP){
		this.log = logP
		this.context = contextP
		this.testRunner = testRunnerP
		// get a reference to the library TestSuite
		this.comlibrary = this.testRunner.testCase.testSuite.project.testSuites["CommonLibrary"]
	}
	def getExcelFileReader(){			
		// find the module within the library 
		def module_ExcelFileReader = comlibrary.testCases["FileHandler"].testSteps["ExcelHandler"]

		// initialise the library; which places an instance of Example in the context
		module_ExcelFileReader.run(testRunner, context)

		//Set context property in setup script so that excelFileReaderTC can be accessed in TestStep
		context.setProperty("excelFileReaderGlobContxt",context.excelFileReader)		
		return context.excelFileReaderGlobContxt
	}
	def getLogger(){			
		// find the module within the library 
		def module_Logger = comlibrary.testCases["ResultLogger"].testSteps["Logger"]

		// initialise the library; which places an instance of Example in the context
		module_Logger.run(testRunner, context)

		//Set context property in setup script so that excelFileReaderTC can be accessed in TestStep
		context.setProperty("loggerGlobContxt",context.logger)		
		return context.loggerGlobContxt
	}


}
//Set GlobalContext context
log.info "GlobalContext called from testCase: " + testRunner.testCase.name
context.setProperty( "globalContext", new GlobalContext( log, context, testRunner ) )

